// client/src/App.jsx
import React, { useState } from 'react';
import PseudoForm from './PseudoForm';
import Chat from './Chat';

function App() {
  const [pseudo, setPseudo] = useState('');

  return (
    <div>
      {pseudo ? (
        <Chat pseudo={pseudo} />
      ) : (
        <PseudoForm setPseudo={setPseudo} />
      )}
    </div>
  );
}

export default App;
